public static class DataUtils {

	public static string LoadFile( string i_strFile ) {
		string strData = "";
#if UNITY_STANDALONE
		string strPath = Application.streamingAssetsPath + "/" + i_strFile + ".json";
		if ( File.Exists( strPath ) ) {
			FileStream file = new FileStream( strPath, FileMode.Open, FileAccess.Read );
			StreamReader sr = new StreamReader( file );
			
			strData = sr.ReadToEnd();
			sr.Close();
			file.Close();
		}
#endif
#if UNITY_WEBPLAYER
		if ( i_strFile == "characters" )
			strData = GetCharacters();
		else if ( i_strFile == "dungeons" )
			strData = GetDungeons();
		else if ( i_strFile == "abilities" )
			strData = GetAbilities();
		else if ( i_strFile == "battles" )
			strData = GetBattles();
		else
			strData = GetItems();
#endif
		return strData;
	}

